	<section>
		<div class="coluna col4 sidebar">
   		<h2 class="aff">Histórias</h2>
      		<img class="resolution" src="img/historia.jpg" alt=""/>
          		<ul>
         			<li>Áudios</li>
            		<li>Quadrinhos</li>
